import React from "react";
import classnames from "classnames";
import { Icon } from "@cdk-uip/react-icon";
import { bool } from "prop-types";

/**
 * Floating Action Button
 */
const FAB = ({ className, mini, exited, children, ...other }) => (
  <button
    className={classnames(
      "mdc-fab",
      {
        "mdc-fab--mini": mini,
        "mdc-fab--exited": exited
      },
      className
    )}
    {...other}
  >
    <Icon className="mdc-fab__icon">{children}</Icon>
  </button>
);

FAB.propTypes = {
  /** Smaller version of the FAB */
  mini: bool,
  /** Animate the FAB out of view */
  exited: bool
};

export default FAB;
