import React from "react";
import { Fluid, FluidItem as RealItem } from ".";
import { storiesOf } from "@storybook/react";
import "./demo.css";

const FluidItem = ({ children, ...rest }) => (
  <RealItem {...rest}>
    <div className="fluid-demo-item">{children}</div>
  </RealItem>
);

const short = <div className="fluid-demo-short">short</div>;
const medium = <div className="fluid-demo-medium">medium</div>;
const tall = <div className="fluid-demo-tall">tall</div>;

storiesOf("fluid", module)
  .add("Plain", () => (
    <Fluid>
      <FluidItem>1</FluidItem>
      <FluidItem>2</FluidItem>
      <FluidItem>3</FluidItem>
    </Fluid>
  ))
  .add("Default", () => (
    <div className="show-fluid">
      <Fluid>
        <FluidItem>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Align items left & right", () => (
    <div className="show-fluid">
      <Fluid>
        <FluidItem>Left</FluidItem>
        <FluidItem>Right</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Fill space", () => (
    <div className="show-fluid">
      <Fluid>
        <FluidItem>Left</FluidItem>
        <FluidItem fill>Fill this space</FluidItem>
        <FluidItem>Right</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Wrap = content", () => (
    <div className="show-fluid">
      <Fluid>
        <FluidItem>
          Lorem ipsum dolor sit amet, et solum ipsum causae his, probatus
          sadipscing mel in.
        </FluidItem>
        <FluidItem>Cum quando nostro pertinax eu.</FluidItem>
        <FluidItem>
          Per in unum probo incorrupte, et nulla saperet intellegat ius.
        </FluidItem>
      </Fluid>
    </div>
  ))
  .add("Wrap = items", () => (
    <div className="show-fluid">
      <Fluid wrap="items">
        <FluidItem>
          Lorem ipsum dolor sit amet, et solum ipsum causae his, probatus
          sadipscing mel in.
        </FluidItem>
        <FluidItem>Cum quando nostro pertinax eu.</FluidItem>
        <FluidItem>
          Per in unum probo incorrupte, et nulla saperet intellegat ius.
        </FluidItem>
      </Fluid>
    </div>
  ))
  .add("Wrap = nowrap", () => (
    <div className="show-fluid">
      <Fluid wrap="nowrap">
        <FluidItem>
          Lorem ipsum dolor sit amet, et solum ipsum causae his, probatus
          sadipscing mel in.
        </FluidItem>
        <FluidItem>Cum quando nostro pertinax eu.</FluidItem>
        <FluidItem>
          Per in unum probo incorrupte, et nulla saperet intellegat ius.
        </FluidItem>
      </Fluid>
    </div>
  ))
  .add("Left h-aligned", () => (
    <div className="show-fluid">
      <Fluid halign="left">
        <FluidItem>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Center h-aligned", () => (
    <div className="show-fluid">
      <Fluid halign="center">
        <FluidItem>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Right h-aligned", () => (
    <div className="show-fluid">
      <Fluid halign="right">
        <FluidItem>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Top v-aligned", () => (
    <div className="show-fluid">
      <Fluid valign="top">
        <FluidItem>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Middle v-aligned", () => (
    <div className="show-fluid">
      <Fluid valign="middle">
        <FluidItem>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Bottom v-aligned", () => (
    <div className="show-fluid">
      <Fluid valign="bottom">
        <FluidItem>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Fixed width", () => (
    <div className="show-fluid">
      <Fluid halign="left">
        <FluidItem style={{ width: "100px" }}>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Percentage width", () => (
    <div className="show-fluid">
      <Fluid halign="left">
        <FluidItem style={{ width: "50%" }}>{short}</FluidItem>
        <FluidItem>{medium}</FluidItem>
        <FluidItem>{tall}</FluidItem>
      </Fluid>
    </div>
  ))
  .add("Within flex", () => (
    <div className="show-fluid fluid-demo-flex">
      <Fluid>
        <FluidItem>1</FluidItem>
        <FluidItem>2</FluidItem>
        <FluidItem>3</FluidItem>
      </Fluid>
    </div>
  ));
