import React from "react";
import classnames from "classnames";
import { bool, oneOf } from "prop-types";

const FluidItem = ({ fill, valign, className, children, ...other }) => {
  return (
    <div
      className={classnames(
        "cdk-fluid__item",
        {
          [`cdk-fluid__item--fill`]: fill,
          [`cdk-fluid__item--valign-${valign}`]: valign
        },
        className
      )}
      {...other}
    >
      {children}
    </div>
  );
};

FluidItem.propTypes = {
  /** Make this item fill any extra space when the container is wider than the items inside. For best results it is recommended to use this for only one item per container. */
  fill: bool,
  /** Override container alignment. */
  valign: oneOf(["stretch", "top", "middle", "bottom"])
};

export default FluidItem;
