import React from "react";
import { FormField } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <FormField id="my-control">
        <input type="text" />
        <label>Label</label>
      </FormField>
    )
  ).toMatchSnapshot();
});

it("renders aligned to the end", () => {
  expect(
    renderer.create(
      <FormField id="my-control" alignEnd>
        <input type="text" />
        <label>Label</label>
      </FormField>
    )
  ).toMatchSnapshot();
});

it("renders a null child", () => {
  class Null extends React.Component {
    render() {
      return null;
    }
  }

  expect(
    renderer.create(
      <FormField id="my-control">
        <input type="text" />
        <label>Label</label>
        <Null />
      </FormField>
    )
  ).toMatchSnapshot();
});
