import React from "react";
import { Form } from ".";
import { Button } from "@cdk-uip/react-button";
import { TextField, TextFieldHelperText } from "@cdk-uip/react-text-field";
import { storiesOf } from "@storybook/react";
import { action } from "@storybook/addon-actions";

storiesOf("form", module)
  .add("required", () => (
    <Form onSubmit={action("onSubmit")}>
      <TextField
        id="name"
        box
        label="Name"
        required
        helperText={
          <TextFieldHelperText validation>
            Please enter your name
          </TextFieldHelperText>
        }
      />

      <Button primary raised type="submit">
        Submit
      </Button>
    </Form>
  ))
  .add("minlength", () => (
    <Form onSubmit={action("onSubmit")}>
      <TextField
        type="text"
        id="name"
        box
        label="Name"
        required
        minLength="3"
        helperText={
          <TextFieldHelperText validation>
            Please enter at least 3 characters
          </TextFieldHelperText>
        }
      />

      <Button primary raised type="submit">
        Submit
      </Button>
    </Form>
  ))
  .add("onInvalid", () => (
    <Form onSubmit={action("onSubmit")} onInvalid={action("onInvalid")}>
      <TextField
        type="text"
        id="name"
        box
        label="Name"
        required
        helperText={
          <TextFieldHelperText>
            Submit the form leaving this field empty
          </TextFieldHelperText>
        }
      />

      <Button primary raised type="submit">
        Submit
      </Button>
    </Form>
  ));
