import React from "react";
import { Toolbar, ToolbarRow } from "@cdk-uip/react-toolbar";
import { bool } from "prop-types";

/**
 * CDK global header.
 */
const GlobalHeader = ({ className, fixed, adjust, children, ...other }) => (
  <Toolbar className="cdk-global-header" fixed={fixed} adjust={adjust}>
    <ToolbarRow>{children}</ToolbarRow>
  </Toolbar>
);

GlobalHeader.propTypes = {
  /** Fix the header to the top of the page. */
  fixed: bool,
  /** Adjust content to take a fixed header into account */
  adjust: bool
};

GlobalHeader.defaultProps = {
  fixed: true
};

export default GlobalHeader;
