import React from "react";
import { GlobalHeaderSection } from ".";

/**
 * Use as a container for `GlobalHeaderIcon` components. The
 * section will appear on the right-hand side of the header
 * and will shrink to fit.
 */
const GlobalHeaderIcons = props => (
  <GlobalHeaderSection align="end" shrinkToFit {...props} />
);

export default GlobalHeaderIcons;
