import React from "react";
import classnames from "classnames";

/**
 * Used to render the application name in the CDK global header.
 */
const GlobalHeaderLabelAppName = ({ className, ...other }) => (
  <span
    className={classnames("cdk-global-header__label-app-name", className)}
    {...other}
  />
);

export default GlobalHeaderLabelAppName;
