import React from "react";
import {
  GridList,
  GridListTiles,
  GridTile,
  GridTilePrimary,
  GridTileSecondary,
  GridTilePrimaryContent,
  GridTileTitle,
  GridTileSupportText,
  GridTileIcon
} from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <GridList
        iconAlign="start"
        tileGutter1
        headerCaption
        twoLineCaption
        aspectRatio="16x9"
      >
        <GridListTiles>
          {[...Array(6).keys()].map(id => (
            <GridTile key={id}>
              <GridTilePrimary>
                <GridTilePrimaryContent src="image.jpg" />
              </GridTilePrimary>
              <GridTileSecondary>
                <GridTileTitle>Title</GridTileTitle>
                <GridTileSupportText>Secondary text</GridTileSupportText>
                <GridTileIcon>star_border</GridTileIcon>
              </GridTileSecondary>
            </GridTile>
          ))}
        </GridListTiles>
      </GridList>
    )
  ).toMatchSnapshot();
});
