import React from "react";
import classnames from "classnames";

/**
 * Tile that can be used to display primary and secondary content.
 * Primary content is often an image and the secondary content is
 * often a caption (with an optional icon.)
 */
const GridTile = ({ className, children, ...other }) => (
  <li className={classnames("mdc-grid-tile", className)} {...other}>
    {children}
  </li>
);

export default GridTile;
