import React from "react";
import classnames from "classnames";

/**
 * Primary tile content. Usually contains a `GridTilePrimaryContent` component.
 */
const GridTilePrimary = ({ className, children, ...other }) => (
  <div className={classnames("mdc-grid-tile__primary", className)} {...other}>
    {children}
  </div>
);

export default GridTilePrimary;
