import React from "react";
import classnames from "classnames";
import { string } from "prop-types";

/**
 * Primary grid tile content, usually an image.
 */
const GridTilePrimaryContent = ({ element, className, children, ...other }) => {
  const T = element;
  return (
    <T
      className={classnames("mdc-grid-tile__primary-content", className)}
      {...other}
    >
      {children}
    </T>
  );
};

GridTilePrimaryContent.propTypes = {
  /** The element type to render. */
  element: string
};

GridTilePrimaryContent.defaultProps = {
  element: "img"
};

export default GridTilePrimaryContent;
