import React from "react";
import classnames from "classnames";

/**
 * Secondary grid tile content. Used to contain `GridTileIcon`,
 * `GridTileTitle` and `GridTileSupportText` components.
 */
const GridTileSecondary = ({ className, children, ...other }) => (
  <span
    className={classnames("mdc-grid-tile__secondary", className)}
    {...other}
  >
    {children}
  </span>
);

export default GridTileSecondary;
