import React from "react";
import classnames from "classnames";
import { Button, ButtonIcon } from "@cdk-uip/react-button";

/**
 * Icon only button.
 *
 * Renders a `Button` with an icon as the only content. All `Button` props
 * can be used.
 */
const IconButton = ({ className, children, ...other }) => (
  <Button className={classnames("mdc-button--icon-only", className)} {...other}>
    <ButtonIcon>{children}</ButtonIcon>
  </Button>
);

export default IconButton;
