import React from "react";
import classnames from "classnames";
import { bool, string } from "prop-types";

const Icon = ({ element, link, className, children, ...other }) => {
  const T = link ? "a" : element;
  return (
    <T
      className={classnames(
        `cdkicon-${children}`,
        { "cdk-icon--link": link },
        className
      )}
      {...other}
    />
  );
};

Icon.propTypes = {
  /** The HTML element to render */
  element: string,
  /** The name of the icon to render */
  children: string,
  /** Render the icon as an <a> */
  link: bool
};

Icon.defaultProps = {
  element: "i"
};

export default Icon;
