import React from "react";
import { Icon } from ".";
import renderer from "react-test-renderer";

it("renders as an <i>", () => {
  expect(renderer.create(<Icon>favorite</Icon>)).toMatchSnapshot();
});

it("renders as an <a>", () => {
  expect(
    renderer.create(
      <Icon link href="#">
        favorite
      </Icon>
    )
  ).toMatchSnapshot();
});
