import React from "react";
import {
  ImageList,
  ImageListItem,
  ImageListAspectContainer,
  ImageListImage,
  ImageListSupporting,
  ImageListLabel
} from ".";
import { storiesOf } from "@storybook/react";
import { withKnobs, boolean, number } from "@storybook/addon-knobs";
import image from "./16-9.jpg";

storiesOf("image-list", module)
  .addDecorator(withKnobs)
  .add("props", () => {
    const labels = boolean("labels below images", true);
    return (
      <ImageList
        columns={number("columns", 5)}
        gutter={number("gutter", 4)}
        textProtection={boolean("textProtection", true)}
      >
        {[...Array(10).keys()].map(index => (
          <ImageListItem key={index}>
            <ImageListAspectContainer>
              <ImageListImage alt="sample" src={image} />
            </ImageListAspectContainer>
            {labels && (
              <ImageListSupporting>
                <ImageListLabel>Image {index + 1}</ImageListLabel>
              </ImageListSupporting>
            )}
          </ImageListItem>
        ))}
      </ImageList>
    );
  });
