import React from "react";
import renderer from "react-test-renderer";
import {
  ImageList,
  ImageListItem,
  ImageListAspectContainer,
  ImageListImage,
  ImageListSupporting,
  ImageListLabel
} from ".";
import image from "./16-9.jpg";

it("renders", () => {
  expect(
    renderer.create(
      <ImageList columns={5} gutter={4} textProtection>
        {[...Array(10).keys()].map(index => (
          <ImageListItem key={index}>
            <ImageListAspectContainer>
              <ImageListImage alt="sample" src={image} />
            </ImageListAspectContainer>
            <ImageListSupporting>
              <ImageListLabel>Image {index + 1}</ImageListLabel>
            </ImageListSupporting>
          </ImageListItem>
        ))}
      </ImageList>
    )
  ).toMatchSnapshot();
});
