import React from "react";
import classnames from "classnames";

/**
 * Used to wrap `<ImageListImage>` components.
 * This element may be omitted entirely if images are already sized to the correct aspect ratio.
 */
const ImageListAspectContainer = ({ className, ...other }) => (
  <div
    className={classnames("mdc-image-list__image-aspect-container")}
    {...other}
  />
);

export default ImageListAspectContainer;
