import React from "react";
import classnames from "classnames";
import { node } from "prop-types";

/**
 * The image to display.
 *
 * Images in an Image List typically use the img element.
 * However, if your assets don't have the same aspect ratio as specified for list items,
 * they will become distorted. In these cases, you can use a div element in place of img,
 * and set the background-image of each using CSS or inline styles.
 */
const ImageListImage = ({ element, className, ...other }) => {
  const T = element;
  return (
    <T className={classnames("mdc-image-list__image", className)} {...other} />
  );
};

ImageListImage.propTypes = {
  /** The element to render. */
  element: node
};

ImageListImage.defaultProps = {
  element: "img"
};

export default ImageListImage;
