import React from "react";
import classnames from "classnames";

/**
 * Represents an item in an image list.
 */
const ImageListItem = ({ className, style, columns, gutter, ...other }) => {
  const mixin = {
    width: `calc(100%/ ${columns} - ${gutter + 1 / columns}px)`,
    margin: gutter / 2
  };
  return (
    <li
      className={classnames("mdc-image-list__item", className)}
      style={{ ...mixin, ...style }}
      {...other}
    />
  );
};

export default ImageListItem;
