import React from "react";
import classnames from "classnames";

/**
 * Indicates the area within each item containing the supporting text label,
 * if the Image List contains text labels.
 */
const ImageListSupporting = ({ className, ...other }) => (
  <div
    className={classnames("mdc-image-list__supporting", className)}
    {...other}
  />
);

export default ImageListSupporting;
