import React from "react";
import { LayoutGrid, LayoutGridCell } from ".";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <LayoutGrid>
        <LayoutGridCell />
        <LayoutGridCell />
        <LayoutGridCell />
      </LayoutGrid>
    )
  ).toMatchSnapshot();
});

it("renders nested", () => {
  expect(
    renderer.create(
      <LayoutGrid>
        <LayoutGridCell>Content</LayoutGridCell>
        <LayoutGridCell>
          <LayoutGrid nested>
            <LayoutGridCell />
          </LayoutGrid>
        </LayoutGridCell>
      </LayoutGrid>
    )
  ).toMatchSnapshot();
});

it("renders for different screen sizes", () => {
  expect(
    renderer.create(
      <LayoutGrid>
        <LayoutGridCell spanDesktop={8} />
        <LayoutGridCell spanTablet={4} />
        <LayoutGridCell spanPhone={2} />
      </LayoutGrid>
    )
  ).toMatchSnapshot();
});
