import React from "react";
import { LinearProgress } from ".";
import renderer from "react-test-renderer";

it("renders indeterminate", () => {
  expect(renderer.create(<LinearProgress indeterminate />)).toMatchSnapshot();
});

it("renders reversed", () => {
  expect(renderer.create(<LinearProgress reversed />)).toMatchSnapshot();
});

it("renders determinate", () => {
  expect(renderer.create(<LinearProgress progress={0.25} />)).toMatchSnapshot();
});
