import React from "react";
import {
  List,
  ListGroup,
  ListItem,
  ListItemText,
  ListSubheader,
  ListItemTextSecondary,
  ListDivider,
  NavList,
  NavListItem,
  ListItemGraphic,
  ListItemMeta
} from ".";
import { Icon } from "@cdk-uip/react-icon";
import { Avatar } from "@cdk-uip/react-avatar";
import renderer from "react-test-renderer";

it("renders a simple list", () => {
  expect(
    renderer.create(
      <List>
        <ListItem>Hungerford</ListItem>
        <ListItem>Portland</ListItem>
        <ListDivider />
        <ListItem>Hoffman Estates</ListItem>
      </List>
    )
  );
});

it("renders", () => {
  expect(
    renderer.create(
      <ListGroup>
        <ListSubheader>Offices</ListSubheader>
        <List multiLine dense interactive>
          {null}
          <ListItem>
            {null}
            <ListItemText>
              Hungerford
              <ListItemTextSecondary>United Kingdon</ListItemTextSecondary>
            </ListItemText>
          </ListItem>
          <ListDivider />
          <ListItem>
            <ListItemText>
              Portland
              <ListItemTextSecondary>United States</ListItemTextSecondary>
            </ListItemText>
          </ListItem>
          <ListDivider />
          <ListItem>
            <ListItemText>
              Hyderabad
              <ListItemTextSecondary>India</ListItemTextSecondary>
            </ListItemText>
          </ListItem>
        </List>
      </ListGroup>
    )
  );
});

it("renders with icons and avatars", () => {
  expect(
    renderer.create(
      <List avatar>
        <ListItem>
          <ListItemGraphic>
            <Avatar>A</Avatar>
          </ListItemGraphic>
          Item 1
          <ListItemMeta>
            <Icon>delete</Icon>
          </ListItemMeta>
        </ListItem>
        <ListItem>
          <ListItemGraphic>
            <Avatar>A</Avatar>
          </ListItemGraphic>
          Item 2
          <ListItemMeta>
            <Icon>delete</Icon>
          </ListItemMeta>
        </ListItem>
      </List>
    )
  ).toMatchSnapshot();
});

it("renders a nav list", () => {
  expect(
    renderer.create(
      <NavList>
        <NavListItem>Item 1</NavListItem>
        <NavListItem>Item 2</NavListItem>
      </NavList>
    )
  ).toMatchSnapshot();
});

it("renders a non-interactive list", () => {
  expect(
    renderer.create(
      <List nonInteractive>
        <ListItem>Hungerford</ListItem>
        <ListItem>Portland</ListItem>
        <ListDivider />
        <ListItem>Hoffman Estates</ListItem>
      </List>
    )
  );
});

it("renders with graphics and metadata", () => {
  expect(
    renderer.create(
      <List avatar>
        <ListItem selected>
          <ListItemGraphic>
            <Avatar>A</Avatar>
          </ListItemGraphic>
          Item 1
          <ListItemMeta>
            <Icon>delete</Icon>
          </ListItemMeta>
        </ListItem>
        <ListItem activated>
          <ListItemGraphic>
            <Avatar>A</Avatar>
          </ListItemGraphic>
          Item 2
          <ListItemMeta>
            <Icon>delete</Icon>
          </ListItemMeta>
        </ListItem>
      </List>
    )
  ).toMatchSnapshot();
});
