import React from "react";

/**
 * Lists can contain graphics at the start of each item.
 * You can use images, avatars, icons, etc.
 *
 * ```js
 * <ListItemGraphic>
 *   <Icon>star</Icon>
 * </ListItemGraphic>
 * ```
 */
class ListItemGraphic extends React.Component {
  render() {
    return React.Children.map(this.props.children, child =>
      React.cloneElement(child, { className: "mdc-list-item__graphic" })
    );
  }
}

export default ListItemGraphic;
