import React from "react";

/**
 * Lists can contain metadata. This can be text, images, etc. and will
 * appear at the end of the list item.
 *
 * ```js
 * <ListItemMeta>
 *   <Icon>star</Icon>
 * </ListItemMeta>
 * ```
 *
 * ```js
 * <ListItemMeta>
 *   <span>Â£1.99</span>
 * </ListItemMeta>
 * ```
 */
class ListItemMeta extends React.Component {
  render() {
    return React.Children.map(this.props.children, child =>
      React.cloneElement(child, { className: "mdc-list-item__meta" })
    );
  }
}

export default ListItemMeta;
