import React from "react";
import List from "./List";

/**
 * Interactive navigation list. Use for lists that allow user interaction
 * via clicks on list item. Use `NavListItem` components for each list item
 * to ensure the correct ripple effect is applied.
 *
 * All `List` props are supported.
 */
const NavList = ({ children, ...other }) => (
  <List interactive {...other}>
    {children}
  </List>
);

export default NavList;
