import React from "react";
import classnames from "classnames";

/**
 * CDK login page.
 *
 * Fully responsive and designed for any screen size.
 *
 * A login page consists of a `Card` containing the login form and
 * an optional `LoginFooter` that usually contains copyright text.
 *
 * You can render your own form components to capture the login
 * details. Extra components allow you to render a logo and a
 * "Forgot password?" link.
 *
 * To apply a background image to the container you can use some
 * custom CSS to apply changes to the `.cdk-login` class. For
 * example:
 *
 * ```css
 * .cdk-login {
 *   background-image: url(./background.jpg);
 *   background-repeat: no-repeat;
 *   background-size: 100% 40%;
 * }
 * ```
 */
const Login = ({ className, children, ...other }) => (
  <div className={classnames("cdk-login", className)} {...other}>
    {children}
  </div>
);

export default Login;
