import React from "react";
import { Login, LoginImage, LoginForgotPassword, LoginFooter } from ".";
import { Card, CardText } from "@cdk-uip/react-card";
import { TextField } from "@cdk-uip/react-text-field";
import { Checkbox } from "@cdk-uip/react-checkbox";
import { FormField } from "@cdk-uip/react-form-field";
import { Button } from "@cdk-uip/react-button";
import renderer from "react-test-renderer";

it("renders", () => {
  expect(
    renderer.create(
      <Login>
        <Card>
          <CardText>
            <LoginImage />
            <TextField
              type="email"
              label="Email or username"
              required
              boxFocus={false}
            />
            <FormField id="remember">
              <Checkbox />
              <label>Remember this</label>
            </FormField>
            <TextField
              type="password"
              label="Password"
              required
              boxFocus={false}
            />
            <LoginForgotPassword>Forgot your password?</LoginForgotPassword>
            <Button primary raised type="submit">
              Sign In
            </Button>
          </CardText>
        </Card>
        <LoginFooter>
          &copy; 2018 CDK Global, CDK Global is a trademark of CDK Global, LLC.
        </LoginFooter>
      </Login>
    )
  ).toMatchSnapshot();
});
