import React from "react";
import classnames from "classnames";

/**
 * Used to render footer text that appears below the login card.
 * You can use this to render a copyright message.
 */
const LoginFooter = ({ className, children, ...other }) => (
  <div className={classnames("cdk-login__footer", className)} {...other}>
    {children}
  </div>
);

export default LoginFooter;
