/* eslint-disable jsx-a11y/alt-text */
import React from "react";
import classnames from "classnames";

/**
 * Used to render a login image (such as a CDK logo) on a login page,
 * ensuring it is styled correctly.
 */
const LoginImage = ({ className, ...other }) => (
  <img className={classnames("cdk-login__logo", className)} {...other} />
);

export default LoginImage;
