import React from "react";
import classnames from "classnames";

/**
 * Menu item. Use in conjunction with a `Menu` for simple menu components.
 */
const MenuItem = ({ className, disabled, children, icon, ...other }) => (
  <li
    className={classnames("mdc-list-item", className)}
    role="menuitem"
    {...(disabled
      ? { "aria-disabled": "true", tabIndex: "-1" }
      : { tabIndex: "0" })}
    {...other}
  >
    {icon && <span className="mdc-menu-item-icon">{icon}</span>}
    {children}
  </li>
);

export default MenuItem;
