import React from "react";
import classnames from "classnames";
import { bool } from "prop-types";

/**
 * This component implements a compact stepper suitable for a mobile device.
 */
const MobileStepper = ({ fixed, className, ...other }) => (
  <div
    className={classnames("cdk-mobile-stepper", {
      "cdk-mobile-stepper--fixed": fixed
    })}
    {...other}
  />
);

MobileStepper.propTypes = {
  /** Fix the stepper to the bottom of the page. */
  fixed: bool
};

export default MobileStepper;
