import React from "react";
import {
  MobileStepper,
  MobileStepperHeader,
  MobileStepperHeaderText,
  MobileStepperBody,
  MobileStepperFooter,
  MobileStepperBackButton,
  MobileStepperNextButton,
  MobileStepperDots,
  MobileStepperProgress
} from ".";
import renderer from "react-test-renderer";

const Stepper = ({ children }) => (
  <MobileStepper>
    <MobileStepperHeader>
      <MobileStepperHeaderText>Header</MobileStepperHeaderText>
    </MobileStepperHeader>
    <MobileStepperBody>Body</MobileStepperBody>
    <MobileStepperFooter>
      <MobileStepperBackButton>Back</MobileStepperBackButton>
      {children}
      <MobileStepperNextButton>Next</MobileStepperNextButton>
    </MobileStepperFooter>
  </MobileStepper>
);

it("renders a text stepper", () => {
  expect(renderer.create(<Stepper />)).toMatchSnapshot();
});

it("renders a dots stepper", () => {
  expect(
    renderer.create(
      <Stepper>
        <MobileStepperDots steps={5} activeStep={3} />
      </Stepper>
    )
  ).toMatchSnapshot();
});

it("renders a progress stepper", () => {
  expect(
    renderer.create(
      <Stepper>
        <MobileStepperProgress steps={5} activeStep={3} />
      </Stepper>
    )
  ).toMatchSnapshot();
});
