import React from "react";
import classnames from "classnames";
import MobileStepperButton from "./MobileStepperButton";
import { ButtonIcon } from "@cdk-uip/react-button";

/**
 * Render a button with a back icon.
 */
const MobileStepperBackButton = ({ className, children, ...other }) => (
  <MobileStepperButton
    className={classnames("cdk-mobile-stepper__button-back", className)}
    {...other}
  >
    <ButtonIcon>chevron_left</ButtonIcon>
    {children}
  </MobileStepperButton>
);

export default MobileStepperBackButton;
