import React from "react";
import MobileStepperDot from "./MobileStepperDot";

/**
 * For use with the `<MobileStepper>` component.
 *
 * Use dots when the number of steps isnâ€™t large.
 */
const MobileStepperDots = ({ steps, activeStep }) => {
  let dots = [];
  for (let i = 0; i < steps; i = i + 1) {
    dots.push(i);
  }

  return (
    <div className="cdk-mobile-stepper__dots">
      {dots.map(index => (
        <MobileStepperDot key={index} active={activeStep === index} />
      ))}
    </div>
  );
};

export default MobileStepperDots;
