import React from "react";
import classnames from "classnames";

/**
 * Render a header that can be used to indicate the current step
 * to a user when using a text-style mobile stepper.
 */
const MobileStepperHeader = ({ className, ...other }) => (
  <header
    className={classnames("cdk-mobile-stepper__header", className)}
    {...other}
  />
);

export default MobileStepperHeader;
