import React from "react";
import classnames from "classnames";

/**
 * Use this to display text in a `MobileStepperHeader`.
 */
const MobileStepperHeaderText = ({ className, ...other }) => (
  <span
    className={classnames("cdk-mobile-stepper__header-text", className)}
    {...other}
  />
);

export default MobileStepperHeaderText;
