import React from "react";
import classnames from "classnames";
import MobileStepperButton from "./MobileStepperButton";
import { ButtonIcon } from "@cdk-uip/react-button";

/**
 * Render a button with a next icon.
 */
const MobileStepperNextButton = ({ className, children, ...other }) => (
  <MobileStepperButton
    className={classnames("cdk-mobile-stepper__button-next", className)}
    {...other}
  >
    {children}
    <ButtonIcon>chevron_right</ButtonIcon>
  </MobileStepperButton>
);

export default MobileStepperNextButton;
