import React from "react";
import { LinearProgress } from "@cdk-uip/react-linear-progress";

/**
 * For use with the `<MobileStepper>` component.
 *
 * Use a progress bar when there are many steps, or if there are steps
 * that need to be inserted during the process
 * (based on responses to earlier steps).
 */
const MobileStepperProgress = ({ steps, activeStep }) => (
  <div className="cdk-mobile-stepper__progress">
    <LinearProgress progress={activeStep / (steps - 1)} />
  </div>
);

export default MobileStepperProgress;
