import React from "react";
import ReactDOM from "react-dom";
import TestUtils from "react-dom/test-utils";
import renderer from "react-test-renderer";
import { NotchedOutline } from "./index";

it("renders", () => {
  expect(renderer.create(<NotchedOutline />)).toMatchSnapshot();
});

it("renders open", () => {
  expect(
    renderer.create(<NotchedOutline open width={200} />)
  ).toMatchSnapshot();
});

it("renders with the correct svg path", () => {
  const node = document.createElement("div");
  const component = ReactDOM.render(<NotchedOutline open width={200} />, node);
  const path = TestUtils.findRenderedDOMComponentWithTag(component, "path");
  expect(path.getAttribute("d")).toMatchSnapshot();
  ReactDOM.render(<NotchedOutline open width={100} />, node);
  expect(path.getAttribute("d")).toMatchSnapshot();
});
