import React from "react";
import { FluidItem } from "@cdk-uip/react-fluid";
import { Button } from "@cdk-uip/react-button";
import classnames from "classnames";
import { func, node, string } from "prop-types";

/**
 * Button to be used within page header actions.
 */
const PageHeaderAction = ({ className, onClick, children, ...others }) => {
  return (
    <FluidItem>
      <Button
        className={classnames("cdk-page-header-action-button", className)}
        {...others}
        onClick={onClick}
      >
        {children}
      </Button>
    </FluidItem>
  );
};

PageHeaderAction.propTypes = {
  /** Page header action button custom CSS class(es) */
  className: string,
  /** Function that is click on Button click */
  onClick: func,
  /** The Button content as a string or component  */
  children: node
  /** Addtionally can recieve any props that can be passed to Button */
};

export default PageHeaderAction;
