import React from "react";
import { FluidItem } from "@cdk-uip/react-fluid";
import { IconButton } from "@cdk-uip/react-icon-button";
import classnames from "classnames";
import { func, node, string } from "prop-types";

/**
 * Icon button to be used within page header actions.
 */
const PageHeaderActionIcon = ({ className, onClick, children, ...others }) => {
  return (
    <FluidItem>
      <IconButton
        className={classnames("cdk-page-header-action-button", className)}
        {...others}
        onClick={onClick}
      >
        {children}
      </IconButton>
    </FluidItem>
  );
};

PageHeaderActionIcon.propTypes = {
  /** Page header icon action custom CSS class(es) */
  className: string,
  /** Function that is click on ButtonIcon click */
  onClick: func,
  /** The name of the MDC icon that will be rendedered as an IconButton */
  children: node
  /** Addtionally can recieve any props that can be passed to IconButton */
};

export default PageHeaderActionIcon;
