import React from "react";
import { node, string } from "prop-types";
import classnames from "classnames";
import { Fluid } from "@cdk-uip/react-fluid";

/**
 * Used to wrap page header actions.
 */
const PageHeaderActions = ({ className, children }) => {
  return (
    <div className={classnames("cdk-page-header-actions", className)}>
      <Fluid>{children}</Fluid>
    </div>
  );
};

PageHeaderActions.propTypes = {
  /** Page header actions custom CSS class(es) */
  className: string,
  /** The page header action(s) or actionIcon(s) or custom component(s) */
  children: node
};

export default PageHeaderActions;
