import React from "react";
import classnames from "classnames";
import { func, node, string } from "prop-types";

/**
 * Used to contain "Back to Previous Page" link on the page header.
 */
const PageHeaderPrevious = ({ className, handleNav, children }) => (
  <div
    className={classnames("cdk-page-header-back-button", className)}
    onClick={handleNav}
  >
    {children || "Back to Previous Page"}
  </div>
);

PageHeaderPrevious.propTypes = {
  /** Page header previous button custom CSS class(es) */
  className: string,
  /** Function which handles the backwards navigation on link click */
  handleNav: func,
  /** Optional text to set, default value is "Back to Previous Page" */
  children: node
};

export default PageHeaderPrevious;
