import React from "react";
import classnames from "classnames";
import { node, string } from "prop-types";

/**
 * Used to create a subtitle for the page header.
 */
const PageHeaderSubtitleText = ({ className, children }) => (
  <div className={classnames("cdk-page-header-subtitle", className)}>
    {children}
  </div>
);

PageHeaderSubtitleText.propTypes = {
  /** Page header subtitle custom CSS class(es) */
  className: string,
  /** Page header subtitle string/component */
  children: node
};
export default PageHeaderSubtitleText;
