import React from "react";
import { Tab } from "@cdk-uip/react-tabs";
import { string } from "prop-types";

/**
 * Used to create a tab for the page header.
 */
const PageHeaderTab = ({ children, ...others }) => {
  return <Tab {...others}>{children}</Tab>;
};

PageHeaderTab.propTypes = {
  /** Page header tab custom CSS class(es) */
  className: string
  /** Additionally can recieve any prop that Tabs component accepts */
};

export default PageHeaderTab;
