import React, { Fragment } from "react";
import { Tabs } from "@cdk-uip/react-tabs";
import classnames from "classnames";
import { string } from "prop-types";

/**
 * Used to wrap page header tabs.
 */
const PageHeaderTabs = ({ className, ...others }) => {
  return (
    <Fragment>
      <hr
        className="mdc-list-divider cdk-page-header-divider"
        key={"cdk-page-header-divider"}
      />
      <div
        key={"cdk-page-header-tabs"}
        className={classnames("cdk-page-header-tabs", className)}
      >
        <Tabs {...others} />
      </div>
    </Fragment>
  );
};

PageHeaderTabs.propTypes = {
  /** Page header tabs custom CSS class(es) */
  className: string
  /** Additionally can recieve any props that Tabs component accepts */
};

export default PageHeaderTabs;
