import React from "react";
import { Fluid, FluidItem } from "@cdk-uip/react-fluid";
import classnames from "classnames";
import { node, string } from "prop-types";

/**
 * Used to set text of the page header title.
 */
const PageHeaderTitle = ({ className, children }) => {
  return (
    <div className={classnames("cdk-page-header-title", className)}>
      <Fluid>
        <FluidItem fill>{children}</FluidItem>
      </Fluid>
    </div>
  );
};

PageHeaderTitle.propTypes = {
  /** Page header title custom CSS class(es) */
  className: string,
  /** Page header title string/component */
  children: node
};

export default PageHeaderTitle;
